"use client";

// import type { Metadata } from "next";
import "./globals.css";
import { Montserrat } from "next/font/google";
import Navbar from "@/components/admin/Navbar";
import { SidebarAdmin, SidebarSiswa } from "@/components/SideBar";
import { usePathname } from "next/navigation";

const montserrat = Montserrat({
  subsets: ["latin"],
  weight: ["100", "200", "300", "400", "500", "600", "700", "800"],
});

// export const metadata: Metadata = {
//   title: "Simon PKL",
//   description: "Generated by create next app",
// };

const disableNavSidebar = ["/login", "/register", "/siswa/home"];

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  const pathname = usePathname();
  const role: string = "admin";
  const backgroundColor = pathname === "/siswa/home" ? "white" : "#EAEAEA";

  // Kondisi hanya /siswa yang dapat mengakses sidebar siswa/publicPage
  const isSiswaPage = pathname?.startsWith("/siswa/");

  return (
    <html lang="en">
      <body
        className={montserrat.className}
        // style={{ backgroundColor: "#EAEAEA" }}
        suppressHydrationWarning={true}
      >
        <div>
          {isSiswaPage && (
            <>
              <Navbar />
              <aside>{SidebarSiswa()}</aside>
            </>
          )}
        </div>
        <div>
          {!disableNavSidebar.includes(pathname)}
          {!disableNavSidebar.includes(pathname) && <Navbar />}
          <div className="flex">
            {!disableNavSidebar.includes(pathname) && <SidebarAdmin />}
            {children}
          </div>
        </div>
      </body>
    </html>
  );
}
